body {
  font-family: "Source Sans Pro", sans-serif;
  background-color: #f2f4f8;
}

.subscriptions {
  max-width: 720px;
  text-align: center;
  margin: 0 auto;
}
.subscriptions form {
  margin: 0 0 1rem 0;
}
.subscriptions h2 {
  text-align: left;
}

.subscriptions--list {
  background-color: white;
  margin-block-start: 0;
  margin-inline-start: 0;
  padding-inline-start: 0;
}

.btn {
  font-family: inherit;
  display: inline-block;
  border: none;
  color: #fff;
  text-decoration: none;
  background-color: #5f3dc4;
  padding: 12px 32px;
  font-size: 14px;
  text-transform: uppercase;
  font-weight: bold;
  letter-spacing: 1px;
  border-radius: 4px;
  text-align: center;
  outline: none;
  border-bottom: 3px solid #392477;
  transition: background-color 0.2s ease-in-out, border-color 0.2s ease-in-out;
  cursor: pointer;
}
.btn:hover {
  background-color: #5536b2;
  border-color: #4c309e;
}
.btn:active {
  background-color: #5536b2;
  border-color: #392477;
}

.input {
  font-size: 1rem;
  padding: 8px 16px;
  vertical-align: middle;
}

.subscriptions--card {
  padding: 1rem;
  display: grid;
  grid-template-columns: min-content 1fr 0.5fr;
  align-items: center;
  border-bottom: 1px solid #E1E3E6;
  padding: 1rem;
}
.subscriptions--card img {
  padding: 0 1rem 0 0;
  max-width: 48px;
}
.subscriptions--card__details {
  text-align: left;
}
.subscriptions--card__details h4 {
  font-size: 1.2rem;
  margin: 0;
}
.subscriptions--card__price {
  text-align: right;
  font-size: 1.2rem;
}

.header {
  color: white;
  display: flex;
  flex-flow: row;
  justify-content: space-between;
  align-items: center;
  background: #364fc7;
  padding: 2rem 1rem;
  border-radius: 0 0 2rem 2rem;
  margin-bottom: 1rem;
}
.header h1 {
  font-weight: 500;
}
.header h2 {
  font-weight: 500;
}

.navbar {
  position: fixed;
  bottom: 0;
  width: 100%;
  background: white;
  padding: 0.8rem 0;
}
.navbar ul {
  margin-block-start: 0;
  margin-block-end: 0;
  padding-inline-start: 0;
  text-align: center;
  display: flex;
  flex-flow: row;
  justify-content: space-around;
  align-items: center;
}
.navbar li {
  color: #ced4da;
  display: inline-block;
  font-size: 2rem;
}

